app.controller("skfCtrl",function($scope,$filter,$timeout,$http){sessionStorage.clear();$scope.loadSlider=function(){$scope.sliderContent=null;$http.get("/PageSetup/GetContentByTypeCode?typeCode=HSlider").then(function(response){response.data&&$timeout(function(){$scope.sliderContent=response.data;$scope.slideLoaded=!0})});$scope.appVersion=null;$http.get("/ApplicationConfiguration/GetAppConfigByName?name=APP_VERSION").then(function(response){response.data&&($scope.appVersion=response.data.appConfigValue)})};$scope.loadSlider()});